    /*functions used in program*/
    public void reset()//reset match for further playing
    {
        j=0;
        jButton1.setBackground(new java.awt.Color(153, 204, 255));
        jButton2.setBackground(new java.awt.Color(153, 204, 255));
        jButton3.setBackground(new java.awt.Color(153, 204, 255));
        jButton4.setBackground(new java.awt.Color(153, 204, 255));
        jButton5.setBackground(new java.awt.Color(153, 204, 255));
        jButton6.setBackground(new java.awt.Color(153, 204, 255));
        jButton7.setBackground(new java.awt.Color(153, 204, 255));
        jButton8.setBackground(new java.awt.Color(153, 204, 255));
        jButton9.setBackground(new java.awt.Color(153, 204, 255));
        if(i%2==0)
            jTextField3.setText("X's TURN");
        else
            jTextField3.setText("O's TURN");
        jTextField4.setText("");
        jButton1.setText("");
        jButton2.setText("");
        jButton3.setText("");
        jButton4.setText("");
        jButton5.setText("");
        jButton6.setText("");
        jButton7.setText("");
        jButton8.setText("");
        jButton9.setText("");
        int p;
        for(p=0;p<9;p++)//resetting array for new match
            a[p]=p+2;
    }
    public void setcolor(javax.swing.JButton jb)
    {
        jb.setBackground(new java.awt.Color(0, 153, 255));
    }
    public void setw(javax.swing.JButton jb)
    {
        jb.setBackground(new java.awt.Color(255, 255, 255));
    }
    public void resetcolor(javax.swing.JButton jb)
    {
        jb.setBackground(new java.awt.Color(153, 204, 255));
    }
    public void button_click(javax.swing.JButton jb,int k)//setting variables when button is clicked
    {
        j++;
        setcolor(jb);
        if((i%2==0&&j%2!=0)||(i%2!=0&&j%2==0))
        {
            jb.setText("X");
            a[k-1]=1;
            jTextField3.setText("O's TURN");
        }
        else
        {
            jb.setText("O");
            a[k-1]=0;
            jTextField3.setText("X's TURN");
        }
        if(j>=5)
            checkwin();
    }
    public void checkwin()//checking if winning condition satisfies or not
    {
        if(a[0]==a[1]&&a[1]==a[2])
        {
            if(a[0]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton1);
            setw(jButton2);
            setw(jButton3);
        }
        else if(a[3]==a[4]&&a[4]==a[5])
        {
            if(a[3]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton4);
            setw(jButton5);
            setw(jButton6);
        }
        else if(a[6]==a[7]&&a[7]==a[8])
        {
            if(a[6]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton7);
            setw(jButton8);
            setw(jButton9);
        }
        else if(a[0]==a[3]&&a[3]==a[6])
        {
            if(a[0]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton1);
            setw(jButton4);
            setw(jButton7);
        }
        else if(a[1]==a[4]&&a[4]==a[7])
        {
            if(a[1]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton2);
            setw(jButton5);
            setw(jButton8);
        }
        else if(a[2]==a[5]&&a[5]==a[8])
        {
            if(a[2]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton3);
            setw(jButton6);
            setw(jButton9);
        }
        else if(a[0]==a[4]&&a[4]==a[8])
        {
            if(a[0]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton1);
            setw(jButton5);
            setw(jButton9);
        }
        else if(a[2]==a[4]&&a[4]==a[6])
        {
            if(a[2]==1)
            {
                jTextField4.setText("PLAYER X WON");
                px++;
            }
            else
            {
                jTextField4.setText("PLAYER 0 WON");
                po++;
            }
            i++;j=-1;
            jTextField1.setText(""+px);
            jTextField2.setText(""+po);
            setw(jButton3);
            setw(jButton5);
            setw(jButton7);
        }
        else if(j==9)
        {
            jTextField4.setText("TIE MATCH");
            pt++;
            jTextField5.setText(""+pt);
            i++;
        }
    }
